import React, { useState } from "react";
import api from "../../api/api";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/auth/login", { username, password });
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.user.role);
      alert("ﾄ斉ハg nh蘯ｭp thﾃnh cﾃｴng");
      window.location.href =
        res.data.user.role === "admin" ? "/admin/dashboard" : "/user/profile";
    } catch (err) {
      alert(err.response?.data?.message || "ﾄ斉ハg nh蘯ｭp th蘯･t b蘯｡i");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "60px" }}>
      <h2>沐 ﾄ斉ハg nh蘯ｭp</h2>
      <form
        onSubmit={handleSubmit}
        style={{ display: "inline-block", textAlign: "left", marginTop: "20px" }}
      >
        <div style={{ marginBottom: "10px" }}>
          <label>Tﾃi kho蘯｣n</label>
          <br />
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: "250px", padding: "8px" }}
            required
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label>M蘯ｭt kh蘯ｩu</label>
          <br />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: "250px", padding: "8px" }}
            required
          />
        </div>
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            background: "#2563eb",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          ﾄ斉ハg nh蘯ｭp
        </button>
      </form>
    </div>
  );
}
