// frontend/src/pages/admin/Courses.jsx
import React, { useEffect, useState } from "react";
import Sidebar from "../../components/Sidebar";
import Header from "../../components/Header";
import api from "../../api/api";

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [form, setForm] = useState({
    maMon: "",
    tenMon: "",
    soTinChi: 3,
    moTa: "",
  });

  const loadCourses = () => {
    api
      .get("/courses")
      .then((res) => setCourses(res.data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    loadCourses();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/courses/${editingId}`, form);
      } else {
        await api.post("/courses", form);
      }
      setForm({ maMon: "", tenMon: "", soTinChi: 3, moTa: "" });
      setEditingId(null);
      loadCourses();
    } catch (err) {
      alert(err.response?.data?.message || "L∆∞u m√¥n h·ªçc th·∫•t b·∫°i");
    }
  };

  const handleEdit = (course) => {
    setEditingId(course._id);
    setForm({
      maMon: course.maMon,
      tenMon: course.tenMon,
      soTinChi: course.soTinChi,
      moTa: course.moTa || "",
    });
  };

  const handleDelete = async (id) => {
    if (!window.confirm("X√≥a m√¥n h·ªçc n√†y?")) return;
    try {
      await api.delete(`/courses/${id}`);
      loadCourses();
    } catch (err) {
      alert(err.response?.data?.message || "X√≥a th·∫•t b·∫°i");
    }
  };

  return (
    <div className="admin-container">
      <Sidebar />
      <Header />
      <main className="admin-main">
        <h2>üìö Qu·∫£n l√Ω m√¥n h·ªçc</h2>

        <form onSubmit={handleSubmit} style={{ marginTop: 20, marginBottom: 20 }}>
          <div>
            <label>M√£ m√¥n</label>
            <input
              name="maMon"
              value={form.maMon}
              disabled={!!editingId}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>T√™n m√¥n</label>
            <input
              name="tenMon"
              value={form.tenMon}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>S·ªë t√≠n ch·ªâ</label>
            <input
              type="number"
              name="soTinChi"
              value={form.soTinChi}
              onChange={handleChange}
              min={1}
            />
          </div>
          <div>
            <label>M√¥ t·∫£</label>
            <textarea
              name="moTa"
              value={form.moTa}
              onChange={handleChange}
              rows={2}
            />
          </div>
          <button type="submit" className="btn-login" style={{ marginTop: 10 }}>
            {editingId ? "C·∫≠p nh·∫≠t" : "Th√™m m·ªõi"}
          </button>
        </form>

        <table>
          <thead>
            <tr>
              <th>M√£ m√¥n</th>
              <th>T√™n m√¥n</th>
              <th>T√≠n ch·ªâ</th>
              <th>M√¥ t·∫£</th>
              <th>H√†nh ƒë·ªông</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((c) => (
              <tr key={c._id}>
                <td>{c.maMon}</td>
                <td>{c.tenMon}</td>
                <td>{c.soTinChi}</td>
                <td>{c.moTa}</td>
                <td>
                  <button onClick={() => handleEdit(c)}>S·ª≠a</button>{" "}
                  <button onClick={() => handleDelete(c._id)}>X√≥a</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
}
