import React, { useState } from "react";
import api from "../../api/api";
import "./Login.css"; // Thêm CSS riêng

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/auth/login", { username, password });
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.user.role);
      alert("Đăng nhập thành công!");
      window.location.href =
        res.data.user.role === "admin" ? "/admin/dashboard" : "/user/profile";
    } catch (err) {
      alert(err.response?.data?.message || "Đăng nhập thất bại");
    }
  };

  return (
    <div className="login-page">
      <div className="login-box">
        <h2 className="login-title">Hệ thống Quản lý Sinh viên</h2>
        <p className="login-subtitle">Vui lòng đăng nhập để tiếp tục</p>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Tên đăng nhập</label>
            <input
              type="text"
              placeholder="Nhập tên đăng nhập"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Mật khẩu</label>
            <input
              type="password"
              placeholder="Nhập mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn-login">
            Đăng nhập
          </button>
        </form>
        <div className="login-footer">
          <p>
            Chưa có tài khoản?{" "}
            <a href="/register" className="link-register">
              Đăng ký ngay
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}
