import React, { useState } from "react";
import axios from "axios";

export default function Register() {
  const [form, setForm] = useState({ username: "", password: "", fullName: "", role: "student" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", form);
      alert("Đăng ký thành công");
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Đăng ký tài khoản</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Họ tên" onChange={(e) => setForm({ ...form, fullName: e.target.value })} />
        <input placeholder="Tài khoản" onChange={(e) => setForm({ ...form, username: e.target.value })} />
        <input type="password" placeholder="Mật khẩu" onChange={(e) => setForm({ ...form, password: e.target.value })} />
        <select onChange={(e) => setForm({ ...form, role: e.target.value })}>
          <option value="student">Sinh viên</option>
          <option value="lecturer">Giảng viên</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">Đăng ký</button>
      </form>
    </div>
  );
}
