// frontend/src/pages/admin/Lecturers.jsx
import React, { useEffect, useState } from "react";
import Sidebar from "../../components/Sidebar";
import Header from "../../components/Header";
import api from "../../api/api";

export default function Lecturers() {
  const [lecturers, setLecturers] = useState([]);
  const [form, setForm] = useState({
    fullName: "",
    username: "",
    password: "",
    lecturerCode: "",
    department: "",
    email: "",
  });

  const loadLecturers = () => {
    api
      .get("/lecturers")
      .then((res) => setLecturers(res.data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    loadLecturers();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/lecturers/create", form);
      alert("T·∫°o gi·∫£ng vi√™n th√†nh c√¥ng");
      setForm({
        fullName: "",
        username: "",
        password: "",
        lecturerCode: "",
        department: "",
        email: "",
      });
      loadLecturers();
    } catch (err) {
      alert(err.response?.data?.message || "T·∫°o gi·∫£ng vi√™n th·∫•t b·∫°i");
    }
  };

  return (
    <div className="admin-container">
      <Sidebar />
      <Header />
      <main className="admin-main">
        <h2>üë®‚Äçüè´ Qu·∫£n l√Ω gi·∫£ng vi√™n</h2>

        <form onSubmit={handleSubmit} style={{ marginTop: 20, marginBottom: 20 }}>
          <div>
            <label>H·ªç t√™n</label>
            <input
              name="fullName"
              value={form.fullName}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>T√™n ƒëƒÉng nh·∫≠p</label>
            <input
              name="username"
              value={form.username}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>M·∫≠t kh·∫©u</label>
            <input
              name="password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>M√£ gi·∫£ng vi√™n</label>
            <input
              name="lecturerCode"
              value={form.lecturerCode}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>B·ªô m√¥n / Khoa</label>
            <input
              name="department"
              value={form.department}
              onChange={handleChange}
            />
          </div>
          <div>
            <label>Email</label>
            <input
              name="email"
              type="email"
              value={form.email}
              onChange={handleChange}
            />
          </div>
          <button type="submit" className="btn-login" style={{ marginTop: 10 }}>
            T·∫°o gi·∫£ng vi√™n
          </button>
        </form>

        <h3>Danh s√°ch gi·∫£ng vi√™n</h3>
        <table>
          <thead>
            <tr>
              <th>M√£ GV</th>
              <th>H·ªç t√™n</th>
              <th>T√™n ƒëƒÉng nh·∫≠p</th>
              <th>Khoa</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {lecturers.map((l) => (
              <tr key={l._id}>
                <td>{l.lecturerCode}</td>
                <td>{l.userId?.fullName}</td>
                <td>{l.userId?.username}</td>
                <td>{l.department}</td>
                <td>{l.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
}
